import React from 'react';

export default function About() {
  const containerStyle = {
    display: 'flex',
    alignItems: 'center',
    justifyContent: 'space-between',
    width: '100%',
    margin: 0,
    padding: 0,
  };

  const photoStyle = {
    width: '50%',
    textAlign: 'left', // photo hugs left edge
  };

  const imgStyle = {
    width: '100%',
    height: 'auto',
    borderRadius: '0',
    objectFit: 'cover',
    display: 'block',
  };

  const textStyle = {
    width: '50%',
    textAlign: 'left', // text aligns left inside its half
    fontFamily: "'Playfair Display', serif",
    color: '#3e2723',
    margin: 0,
    paddingLeft: '2rem', // adds space between photo & text
  };

  return (
    <div style={containerStyle}>
      {/* Photo on the left */}
      <div style={photoStyle}>
        <img src="/me.jpg" alt="Anu Kherlen" style={imgStyle} />
      </div>

      {/* Text on the right */}
      <div style={textStyle}>
        <p style={{ margin: 0 }}>
          Anu Kherlen is a photographer based in Chicago, IL. She used to believe that writers were the only storytellers.
          Art invalidated that notion, because storytelling is the act of weaving little bits of information to make a bigger thing.
          Her photographs and other mediums of art are her own version of storytelling.
        </p>
      </div>
    </div>
  );
}
