import React, { useState, useEffect } from 'react';
import { useAuth0 } from '@auth0/auth0-react';
import AlbumForm from './AlbumForm';
import axios from 'axios';

const Albums = () => {
  const { user, isAuthenticated, isLoading, getAccessTokenSilently } = useAuth0();
  const [albums, setAlbums] = useState([]);

  const fetchAlbums = async () => {
    try {
      const token = await getAccessTokenSilently();
      const response = await axios.get('/api/albums', {
        headers: {
          Authorization: `Bearer ${token}`,
        },
      });
      setAlbums(response.data);
    } catch (err) {
      console.error("Failed to fetch albums:", err);
    }
  };

  useEffect(() => {
    if (isAuthenticated) {
      fetchAlbums();
    }
  }, [isAuthenticated]);

  // More robust loading check
  if (isLoading || !user) {
    return <div>Loading...</div>;
  }

  if (!isAuthenticated) {
    return <div>Please log in to view albums.</div>;
  }

  // Extract roles with more defensive programming
  const roles = user['https://anukaphotos333/roles'] || [];
  const isAdmin = Array.isArray(roles) && roles.includes('Admin');

  return (
    <div style={{ padding: '20px' }}>
      <h1>Albums</h1>

      {/* Show the form if user is admin */}
      {isAdmin && <AlbumForm onAlbumCreated={fetchAlbums} />}

      {/* List all albums */}
      <div style={{ marginTop: '20px' }}>
        {albums.length === 0 ? (
          <p>No albums available.</p>
        ) : (
          <ul style={{ listStyle: 'none', padding: 0 }}>
            {albums.map((album) => (
              <li key={album.id} style={{ marginBottom: '20px' }}>
                <h3>{album.name}</h3>
                <div style={{ display: 'flex', gap: '10px', flexWrap: 'wrap' }}>
                  {album.photos.map((photo, idx) => (
                    <img
                      key={idx}
                      src={photo.url}
                      alt={photo.name}
                      style={{ width: '150px', height: '150px', objectFit: 'cover', borderRadius: '5px' }}
                    />
                  ))}
                </div>
              </li>
            ))}
          </ul>
        )}
      </div>
    </div>
  );
};

export default Albums;
