import { useState } from 'react';
import { useAuth0 } from '@auth0/auth0-react';

const PhotoUploader = ({ galleryId, onUploadComplete }) => {
  const [photos, setPhotos] = useState([]);
  const [previews, setPreviews] = useState([]);
  const [loading, setLoading] = useState(false);

  const { getAccessTokenSilently } = useAuth0();



  const handlePhotoChange = (e) => {
    const files = Array.from(e.target.files);
    setPhotos(files);
    setPreviews(files.map((file) => URL.createObjectURL(file)));
  };

  const uploadFile = async (file, galleryId) => {
    const formData = new FormData();
    formData.append('file', file);

    try {
      const token = await getAccessTokenSilently({ audience: process.env.REACT_APP_AUTH0_AUDIENCE });
      const response = await fetch(`http://127.0.0.1:5000/gallery/${galleryId}/upload`, {
        method: 'POST',
        headers: {
          'Authorization': `Bearer ${token}`
        },
        body: formData
      });

      if (!response.ok) {
        throw new Error(`Upload failed: ${response.statusText}`);
      }

      const data = await response.json();
      console.log('Upload response:', data);
      return data.url;
    } catch (error) {
      console.error('Error uploading file:', error);
      throw error;
    }
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    if (!albumName || photos.length === 0 || !clientId) {
      alert('Please provide album name, select a client, and at least one photo.');
      return;
    }

    try {
      setLoading(true);
      const token = await getAccessTokenSilently({ audience: process.env.REACT_APP_AUTH0_AUDIENCE });

      // First, create the album
      const albumData = {
        name: albumName,
        clientId: clientId
      };

      // Create album and get its ID
      const albumResponse = await api.post('/api/albums/create', albumData, {
        headers: { 
          'Content-Type': 'application/json',
          'Authorization': `Bearer ${token}`
        },
      });

      const galleryId = albumResponse.data.id;

      // Upload each photo to the created album
      const uploadPromises = photos.map(photo => uploadFile(photo, galleryId));

      try {
        await Promise.all(uploadPromises);
        console.log('All photos uploaded successfully');
        onAlbumCreated(); // refresh albums list
      } catch (uploadError) {
        console.error('Error uploading photos:', uploadError);
        alert('Some photos failed to upload. Please try again.');
        // You might want to delete the created album here if photos fail to upload
        return;
      }

      console.log('Album created and photos uploaded successfully');

      // Reset form
      setAlbumName('');
      setPhotos([]);
      setPreviews([]);
      setClientId('');
    } catch (error) {
      console.error('Error creating album:', error);
      alert('Error creating album.');
    } finally {
      setLoading(false);
    }
  };

  return (
    <div style={{ marginTop: '20px', border: '1px solid #ccc', padding: '15px', borderRadius: '8px' }}>
      <h2>Create New Album</h2>
      <form onSubmit={handleSubmit}>
        <div style={{ marginBottom: '10px' }}>
          <label>
            Select Client:
            <select value={clientId} onChange={e => setClientId(e.target.value)} required style={{ marginLeft: '10px' }}>
              <option value="">-- Select Client --</option>
              {users.map(user => (
                <option key={user.id || user.auth0_id || user.email} value={user.id || user.auth0_id || user.email}>
                  {user.name ? `${user.name} (${user.email || user.auth0_id || ''})` : (user.email || user.auth0_id)}
                </option>
              ))}
            </select>
          </label>
        </div>
        <div>
          <label>
            Album Name:
            <input
              type="text"
              value={albumName}
              onChange={(e) => setAlbumName(e.target.value)}
              required
              style={{ marginLeft: '10px' }}
            />
          </label>
        </div>

        <div style={{ marginTop: '10px' }}>
          <label>
            Upload Photos:
            <input
              type="file"
              multiple
              accept="image/*"
              onChange={handlePhotoChange}
              style={{ marginLeft: '10px' }}
            />
          </label>
        </div>

        {previews.length > 0 && (
          <div style={{ marginTop: '10px', display: 'flex', gap: '10px', flexWrap: 'wrap' }}>
            {previews.map((url, index) => (
              <img key={index} src={url} alt={`Preview ${index}`} style={{ width: '100px', height: '100px', objectFit: 'cover', borderRadius: '5px' }} />
            ))}
          </div>
        )}

        <button type="submit" disabled={loading} style={{ marginTop: '10px', padding: '6px 12px' }}>
          {loading ? 'Uploading...' : 'Create Album'}
        </button>
      </form>
    </div>
  );
};

export default AlbumForm;
