import React, { useState } from 'react';
import { useOutletContext } from 'react-router-dom';

export default function Portraits() {
  const { editMode } = useOutletContext() || {};

  // State for editable captions
  const [caption1, setCaption1] = useState('Portrait 1');
  const [caption2, setCaption2] = useState('Portrait 2');

  return (
    <div style={{ display: 'flex', flexWrap: 'wrap', justifyContent: 'center', gap: '20px' }}>
      <div style={{ textAlign: 'center' }}>
        <img src="/elizabeth.jpg" alt="Portrait 1" style={{ width: '300px' }} />
        {editMode ? (
          <input
            type="text"
            value={caption1}
            onChange={(e) => setCaption1(e.target.value)}
            style={{ width: '100%', marginTop: '5px', textAlign: 'center' }}
          />
        ) : (
          <p>{caption1}</p>
        )}
      </div>

      <div style={{ textAlign: 'center' }}>
        <img src="/potrait.jpg" alt="Portrait 2" style={{ width: '300px' }} />
        {editMode ? (
          <input
            type="text"
            value={caption2}
            onChange={(e) => setCaption2(e.target.value)}
            style={{ width: '100%', marginTop: '5px', textAlign: 'center' }}
          />
        ) : (
          <p>{caption2}</p>
        )}
      </div>
    </div>
  );
}
