import axios from 'axios';

// Use localhost backend automatically in development to avoid needing env setup.
const devDefault = process.env.NODE_ENV === 'development' ? 'http://localhost:5001' : '';
const API_BASE = process.env.REACT_APP_API_URL || devDefault;

const api = axios.create({
  baseURL: API_BASE,
});

export default api;
