import React, { useState, useEffect } from 'react';
import { useAuth0 } from '@auth0/auth0-react';
import AlbumForm from './AlbumForm';
import axios from 'axios';

const Albums = () => {
  const { user, isAuthenticated } = useAuth0();
  const [albums, setAlbums] = useState([]);

  // DEBUG: Check user object and roles
  console.log('Auth0 user:', user);

  const roles = user?.['https://anukaphotos333/roles'] || [];
  const isAdmin = roles.includes('Admin');

  const fetchAlbums = async () => {
    try {
      const response = await axios.get('/api/albums'); // adjust endpoint
      setAlbums(response.data);
    } catch (err) {
      console.error('Error fetching albums:', err);
    }
  };

  useEffect(() => {
    if (isAuthenticated) {
      fetchAlbums();
    }
  }, [isAuthenticated]);

  return (
    <div style={{ padding: '20px' }}>
      <h1>Albums</h1>

      {/* Admin can create albums */}
      {isAdmin && <AlbumForm onAlbumCreated={fetchAlbums} />}

      {/* Display albums */}
      <div style={{ marginTop: '20px' }}>
        {albums.length === 0 ? (
          <p>No albums available.</p>
        ) : (
          <ul style={{ listStyle: 'none', padding: 0 }}>
            {albums.map((album) => (
              <li key={album.id} style={{ marginBottom: '20px' }}>
                <h3>{album.name}</h3>
                <div style={{ display: 'flex', flexWrap: 'wrap', gap: '10px' }}>
                  {album.photos.map((photo, idx) => (
                    <img key={idx} src={photo.url} alt={photo.name} style={{ width: '150px', height: '150px', objectFit: 'cover', borderRadius: '5px' }} />
                  ))}
                </div>
              </li>
            ))}
          </ul>
        )}
      </div>
    </div>
  );
};

export default Albums;
