import React, { useState, useEffect } from 'react';
import { useAuth0 } from '@auth0/auth0-react';
import axios from 'axios';
import AlbumForm from './AlbumForm';

const Albums = () => {
  // Destructure all necessary values from the Auth0 hook, including isLoading
  const { user, isAuthenticated, isLoading, getAccessTokenSilently } = useAuth0();

  const [albums, setAlbums] = useState([]);
  const [error, setError] = useState('');

  /**
   * Fetches albums from the secure backend API.
   * It gets a JWT (Access Token) from Auth0 and includes it in the request header.
   * Your backend will use this token to identify the user and their role.
   */
  const fetchAlbums = async () => {
    try {
      const token = await getAccessTokenSilently();
      const response = await axios.get('/api/albums', {
        headers: {
          Authorization: `Bearer ${token}`,
        },
      });
      setAlbums(response.data);
    } catch (err) {
      console.error("Failed to fetch albums:", err);
      setError('Could not load albums. Please try again later.');
    }
  };

  // The useEffect hook runs when the component mounts.
  // It will only fetch albums if the user is successfully authenticated.
  useEffect(() => {
    if (isAuthenticated) {
      fetchAlbums();
    }
  }, [isAuthenticated]); // The dependency array ensures this runs only when isAuthenticated changes.

  // 1. CRUCIAL: Display a loading message until Auth0 has finished initializing.
  // This prevents race conditions where the component renders before 'user' is populated.
  if (isLoading) {
    return <div style={{ padding: '20px', textAlign: 'center' }}>Loading...</div>;
  }

  // 2. If not loading and not authenticated, prompt the user to log in.
  if (!isAuthenticated || !user) {
    return <div style={{ padding: '20px', textAlign: 'center' }}>Please log in to view your albums.</div>;
  }

  // 3. Safely extract roles from the user object.
  const roles = user['https://anukaphotos333/roles'] || [];

  // 4. Use a more robust check for the 'Admin' role to avoid issues with whitespace or casing.
  const isAdmin = roles.some(role => role.trim().toLowerCase() === 'admin');

  return (
    <div style={{ padding: '20px' }}>
      <h1>Albums</h1>

      {/* Conditionally render the form ONLY for admin users */}
      {isAdmin && <AlbumForm onAlbumCreated={fetchAlbums} />}

      {/* Display an error message if the API call failed */}
      {error && <p style={{ color: 'red' }}>{error}</p>}

      {/* Display the list of albums */}
      <div style={{ marginTop: '20px' }}>
        {!error && albums.length === 0 ? (
          <p>No albums available.</p>
        ) : (
          <ul style={{ listStyle: 'none', padding: 0 }}>
            {albums.map((album) => (
              <li key={album.id} style={{ marginBottom: '20px', border: '1px solid #eee', padding: '15px', borderRadius: '8px' }}>
                <h3>{album.name}</h3>
                <div style={{ display: 'flex', gap: '10px', flexWrap: 'wrap' }}>
                  {album.photos && album.photos.map((photo, idx) => (
                    <img
                      key={idx}
                      src={photo.url}
                      alt={photo.name}
                      style={{ width: '150px', height: '150px', objectFit: 'cover', borderRadius: '5px' }}
                    />
                  ))}
                </div>
              </li>
            ))}
          </ul>
        )}
      </div>
    </div>
  );
};

export default Albums;
