import React, { useState } from "react";
import { useAuth0 } from "@auth0/auth0-react";
import { useNavigate } from "react-router-dom";

export default function Layout({ children }) {
  const { user, loginWithRedirect, logout, isAuthenticated, isLoading } = useAuth0();
  const [editMode, setEditMode] = useState(false);
  const navigate = useNavigate();

  if (isLoading) return <div>Loading...</div>;

  const toggleEditMode = () => setEditMode((prev) => !prev);

  const roles = user?.["https://anukaphotos333/roles"] || [];
  const isAdmin = roles.includes("Admin");

  return (
    <div>
      <header style={{ display: "flex", justifyContent: "space-between", padding: "10px" }}>
        <h1>Anuka Photos</h1>
        <div>
          {!isAuthenticated && (
            <button onClick={() => loginWithRedirect()}>Log In</button>
          )}

          {isAuthenticated && (
            <>
              <span style={{ marginRight: "10px" }}>{user.name}</span>

              {isAdmin && (
                <button onClick={toggleEditMode} style={{ marginRight: "10px" }}>
                  {editMode ? "Exit Edit Mode" : "Edit Page"}
                </button>
              )}

              <button
                onClick={() => {
                  logout({ returnTo: window.location.origin });
                  navigate("/");
                }}
              >
                Log Out
              </button>
            </>
          )}
        </div>
      </header>

      <main>
        {React.isValidElement(children)
          ? React.cloneElement(children, { editMode })
          : children}
      </main>
    </div>
  );
}
