import React, { useState } from 'react';
import { Outlet } from 'react-router-dom';
import NavBar from './components/nav/NavBar';
import { useAuth0 } from '@auth0/auth0-react';
import './layout.css';

const Layout = () => {
  const { loginWithRedirect, logout, isAuthenticated, user } = useAuth0();

  // State to track edit mode
  const [editMode, setEditMode] = useState(false);
  const toggleEditMode = () => setEditMode(!editMode);

  return (
    <div>
      <header className="header-row">
        <img src="/anuka.png" alt="Anuka Photos Banner" className="banner-img" />

        <div className="my-account-link">
          {isAuthenticated ? (
            <>
              <span>Hello, Anu</span>
              <button onClick={() => logout({ returnTo: window.location.origin })}>
                Logout
              </button>
            </>
          ) : (
            <button onClick={() => loginWithRedirect()}>My Account</button>
          )}
        </div>

        <NavBar />
      </header>

      {/* Admin Edit Page Button */}
      {isAuthenticated && user?.['https://anukaphotos333/roles']?.includes('admin') && (
        <div style={{ textAlign: 'center', marginTop: '100px' /* below header */ }}>
          <button onClick={toggleEditMode}>
            {editMode ? 'Exit Edit Mode' : 'Edit Page'}
          </button>
        </div>
      )}

      <main>
        {/* Pass editMode to child pages if needed */}
        <Outlet context={{ editMode }} />
      </main>
    </div>
  );
};

export default Layout;
