import React, { useState, useEffect } from 'react';
import { useAuth0 } from '@auth0/auth0-react';
import AlbumForm from './AlbumForm';
import axios from 'axios';

const Albums = () => {
  const { user, isAuthenticated } = useAuth0();
  const [roles, setRoles] = useState([]);
  const [albums, setAlbums] = useState([]);

  useEffect(() => {
    if (isAuthenticated && user) {
      setRoles(user['https://anukaphotos333/roles'] || []);
    }
  }, [isAuthenticated, user]);

  const fetchAlbums = async () => {
    try {
      const response = await axios.get('/api/albums'); // adjust endpoint
      setAlbums(response.data);
    } catch (err) {
      console.error(err);
    }
  };

  useEffect(() => {
    if (isAuthenticated) {
      fetchAlbums();
    }
  }, [isAuthenticated]);

  return (
    <div>
      <h1>Albums</h1>

      {/* Show form only for Admin */}
      {isAuthenticated && roles.includes('Admin') && (
        <AlbumForm onAlbumCreated={fetchAlbums} />
      )}

      {/* List albums for all users */}
      <div style={{ marginTop: '20px' }}>
        {albums.length === 0 ? (
          <p>No albums available.</p>
        ) : (
          <ul>
            {albums.map((album) => (
              <li key={album.id}>
                <h3>{album.name}</h3>
                {album.photos.map((photo, idx) => (
                  <img
                    key={idx}
                    src={photo.url}
                    alt={photo.name}
                    style={{ width: '150px', marginRight: '10px', marginBottom: '10px' }}
                  />
                ))}
              </li>
            ))}
          </ul>
        )}
      </div>
    </div>
  );
};
export default Albums;
