import React from 'react';

export default function About() {
  const containerStyle = {
    display: 'flex',
    flexWrap: 'wrap',
    alignItems: 'center',
    justifyContent: 'center',
    padding: '2rem',
    maxWidth: '900px',
    margin: 'auto',
    gap: '2rem',
  };

  const textStyle = {
    flex: '1 1 300px',
    textAlign: 'center',
    fontFamily: "'Playfair Display', serif",
    color: '#3e2723',
  };

  const photoStyle = {
    flex: '1 1 300px',
    textAlign: 'center',
  };

  const imgStyle = {
    maxWidth: '100%',
    height: 'auto',
    borderRadius: '10px',
    objectFit: 'cover',
  };

  return (
    <div style={containerStyle}>
      <div style={textStyle}>
        <p>
          Anu Kherlen is a photographer based in Chicago, IL. She used to believe that writers were the only storytellers.
          Art invalidated that notion, because storytelling is the act of weaving little bits of information to make a bigger thing.
          Her photographs and other mediums of art are her own version of storytelling.
        </p>
      </div>
      <div style={photoStyle}>
        <img src="/AnuAbout.jpg" alt="Anu Kherlen" style={imgStyle} />
      </div>
    </div>
  );
}
