import React, { useState, useEffect } from 'react';
import axios from 'axios';
import api from '../api';
import { useAuth0 } from '@auth0/auth0-react';

const AlbumForm = ({ onAlbumCreated }) => {
  const [albumName, setAlbumName] = useState('');
  const [photos, setPhotos] = useState([]);
  const [previews, setPreviews] = useState([]);
  const [loading, setLoading] = useState(false);
  const [users, setUsers] = useState([]);
  const [clientAuth0Id, setClientAuth0Id] = useState('');

  const { getAccessTokenSilently } = useAuth0();

  // Fetch users for admin dropdown
  useEffect(() => {
    async function fetchUsers() {
      try {
        // Try to obtain an access token (may fail if audience not configured)
        let token;
        try {
          token = await getAccessTokenSilently({ audience: process.env.REACT_APP_AUTH0_AUDIENCE });
        } catch (tErr) {
          console.warn('getAccessTokenSilently failed:', tErr?.message || tErr);
        }

        // In development, call the localhost backend directly to avoid host/proxy issues.
        const backendUrl = (process.env.NODE_ENV === 'development') ? 'http://localhost:5001' : '';
        const usersEndpoint = backendUrl ? `${backendUrl}/auth/users` : '/auth/users';

        let res;
        if (token) {
          res = await axios.get(usersEndpoint, {
            headers: { Authorization: `Bearer ${token}` },
          });
        } else {
          // fallback: try unauthenticated call (useful when backend route is public or for debugging path issues)
          console.warn('No token available  attempting unauthenticated request to /auth/users');
          res = await axios.get(usersEndpoint);
        }

        console.log('users response:', res?.data);
        const usersArr = res.data?.users || res.data || [];
        setUsers(usersArr);
        console.log('users state set (count):', usersArr.length, usersArr.slice(0,5));
      } catch (err) {
        console.error('Failed to fetch users:', err?.response?.data || err.message || err);
      }
    }

    fetchUsers();
  }, [getAccessTokenSilently]);

  const handlePhotoChange = (e) => {
    const files = Array.from(e.target.files);
    setPhotos(files);
    setPreviews(files.map((file) => URL.createObjectURL(file)));
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
  if (!albumName || photos.length === 0 || !clientAuth0Id) {
      alert('Please provide album name, select a client, and at least one photo.');
      return;
    }

    const formData = new FormData();
    formData.append('albumName', albumName);
  formData.append('clientAuth0Id', clientAuth0Id);
    photos.forEach((photo) => formData.append('photos', photo));

    try {
      setLoading(true);
      const token = await getAccessTokenSilently({ audience: process.env.REACT_APP_AUTH0_AUDIENCE });

      // 1) Create album record (JSON body)
      const createRes = await api.post(
        '/api/albums/create',
        { albumName, clientAuth0Id },
        { headers: { Authorization: `Bearer ${token}` } }
      );
      const albumId = createRes.data.albumId || createRes.data.id;
      console.log('Album created:', createRes.data);

      // 2) Upload each photo to the album using the upload endpoint
      const uploadPromises = photos.map((photo) => {
        const fd = new FormData();
        fd.append('file', photo);
        return api.post(`/api/albums/${albumId}/upload`, fd, {
          headers: { Authorization: `Bearer ${token}` },
        });
      });

      const uploadResults = await Promise.all(uploadPromises);
      console.log('Uploaded photos:', uploadResults.map(r => r.data));

      onAlbumCreated(); // refresh albums list

      // Reset form
      setAlbumName('');
      setPhotos([]);
      setPreviews([]);
  setClientAuth0Id('');
    } catch (error) {
      console.error('Error creating album:', error);
      alert('Error creating album.');
    } finally {
      setLoading(false);
    }
  };

  return (
    <div style={{ marginTop: '20px', border: '1px solid #ccc', padding: '15px', borderRadius: '8px' }}>
      <h2>Create New Album</h2>
      <form onSubmit={handleSubmit}>
        <div style={{ marginBottom: '10px' }}>
          <label>
            Select Client:
            <select value={clientAuth0Id} onChange={e => setClientAuth0Id(e.target.value)} required style={{ marginLeft: '10px' }}>
              <option value="">-- Select Client --</option>
              {users.map(user => (
                <option key={user.id} value={user.auth0_id || user.id}>
                  {user.name} ({user.email}) {user.auth0_id ? ` — ${user.auth0_id}` : ''}
                </option>
              ))}
            </select>
          </label>
        </div>
        <div>
          <label>
            Album Name:
            <input
              type="text"
              value={albumName}
              onChange={(e) => setAlbumName(e.target.value)}
              required
              style={{ marginLeft: '10px' }}
            />
          </label>
        </div>

        <div style={{ marginTop: '10px' }}>
          <label>
            Upload Photos:
            <input
              type="file"
              multiple
              accept="image/*"
              onChange={handlePhotoChange}
              style={{ marginLeft: '10px' }}
            />
          </label>
        </div>

        {previews.length > 0 && (
          <div style={{ marginTop: '10px', display: 'flex', gap: '10px', flexWrap: 'wrap' }}>
            {previews.map((url, index) => (
              <img key={index} src={url} alt={`Preview ${index}`} style={{ width: '100px', height: '100px', objectFit: 'cover', borderRadius: '5px' }} />
            ))}
          </div>
        )}

        <button type="submit" disabled={loading} style={{ marginTop: '10px', padding: '6px 12px' }}>
          {loading ? 'Uploading...' : 'Create Album'}
        </button>
      </form>
    </div>
  );
};

export default AlbumForm;
