import { useState } from 'react';
import { useAuth0 } from '@auth0/auth0-react';

const PhotoUploader = ({ galleryId, onUploadComplete }) => {
  const [photos, setPhotos] = useState([]);
  const [previews, setPreviews] = useState([]);
  const [loading, setLoading] = useState(false);

  const { getAccessTokenSilently } = useAuth0();



  const handlePhotoChange = (e) => {
    const files = Array.from(e.target.files);
    setPhotos(files);
    setPreviews(files.map((file) => URL.createObjectURL(file)));
  };

  const uploadFile = async (file) => {
    const formData = new FormData();
    formData.append('file', file);

    try {
      const token = await getAccessTokenSilently({ audience: process.env.REACT_APP_AUTH0_AUDIENCE });
      const response = await fetch(`http://127.0.0.1:5000/gallery/${galleryId}/upload`, {
        method: 'POST',
        headers: {
          'Authorization': `Bearer ${token}`
        },
        body: formData
      });

      if (!response.ok) {
        throw new Error(`Upload failed: ${response.statusText}`);
      }

      const data = await response.json();
      console.log('Upload response:', data);
      return data.url;
    } catch (error) {
      console.error('Error uploading file:', error);
      throw error;
    }
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    if (photos.length === 0) {
      alert('Please select at least one photo to upload.');
      return;
    }

    try {
      setLoading(true);

      // Upload all photos in parallel
      const uploadPromises = photos.map(photo => uploadFile(photo));

      try {
        await Promise.all(uploadPromises);
        console.log('All photos uploaded successfully');
        onUploadComplete?.(); // Notify parent component if needed
      } catch (uploadError) {
        console.error('Error uploading photos:', uploadError);
        alert('Some photos failed to upload. Please try again.');
        return;
      }

      // Reset form
      setPhotos([]);
      setPreviews([]);
    } catch (error) {
      console.error('Error uploading photos:', error);
      alert('Error uploading photos.');
    } finally {
      setLoading(false);
    }
  };

  return (
    <div style={{ marginTop: '20px', border: '1px solid #ccc', padding: '15px', borderRadius: '8px' }}>
      <h2>Upload Photos</h2>
      <form onSubmit={handleSubmit}>

        <div style={{ marginTop: '10px' }}>
          <label>
            Upload Photos:
            <input
              type="file"
              multiple
              accept="image/*"
              onChange={handlePhotoChange}
              style={{ marginLeft: '10px' }}
            />
          </label>
        </div>

        {previews.length > 0 && (
          <div style={{ marginTop: '10px', display: 'flex', gap: '10px', flexWrap: 'wrap' }}>
            {previews.map((url, index) => (
              <img key={index} src={url} alt={`Preview ${index}`} style={{ width: '100px', height: '100px', objectFit: 'cover', borderRadius: '5px' }} />
            ))}
          </div>
        )}

        <button type="submit" disabled={loading} style={{ marginTop: '10px', padding: '6px 12px' }}>
          {loading ? 'Uploading...' : 'Create Album'}
        </button>
      </form>
    </div>
  );
};

export default AlbumForm;
