import React from 'react';

export default function About() {
  const containerStyle = {
    display: 'flex',
    flexWrap: 'wrap',
    alignItems: 'center',
    justifyContent: 'space-between',
    padding: '2rem',
    maxWidth: '1100px',
    margin: 'auto',
    gap: '2rem',
  };

  const photoStyle = {
    flex: '1 1 400px',
    textAlign: 'center',
  };

  const imgStyle = {
    maxWidth: '100%',
    height: 'auto',
    borderRadius: '10px',
    objectFit: 'cover',
  };

  const textStyle = {
    flex: '1 1 500px',
    textAlign: 'right',
    fontFamily: "'Playfair Display', serif",
    color: '#3e2723',
    margin: 0,
  };

  return (
    <div style={containerStyle}>
      {/* Photo on the left */}
      <div style={photoStyle}>
        <img src="/AnuAbout.jpg" alt="Anu Kherlen" style={imgStyle} />
      </div>

      {/* Text on the right */}
      <div style={textStyle}>
        <p style={{ margin: 0 }}>
          Anu Kherlen is a photographer based in Chicago, IL. She used to believe that writers were the only storytellers.
          Art invalidated that notion, because storytelling is the act of weaving little bits of information to make a bigger thing.
          Her photographs and other mediums of art are her own version of storytelling.
        </p>
      </div>
    </div>
  );
}
